import pandas as pd
from datetime import datetime, timedelta
import random

num_users = 400
users = [f"user_{i}" for i in range(1, num_users + 1)]

interactions = []
base_date = datetime.now() - timedelta(weeks= 220) 


# Define realistic interaction sequences and their likelihood
interaction_sequences = [
    ["view"],
    ["view", "add to cart"],
    ["add to cart"],
    ["add to wishlist"],
    ["add to compare"],
    ["view", "add to wishlist"],
    ["view", "add to compare"],
    ["view", "add to cart", "review"],
    ["view", "add to compare", "add to cart", "review"]

]
interaction_sequences 
weights = [0.35, 0.1, 0.1, 0.1, 0.1, 0.07, 0.07, 0.06, 0.05]

all_items = [electronics , home_appliances,fashion,beauty_personal_care,sports_outdoors,Automotive,Books_and_Stationery,Grocery_and_Gourmet_Food,Health_and_Wellness,Toys_and_Games]


for user in users:
    base_date = datetime.now() - timedelta(weeks= 220) 
    random.shuffle(all_items)
    for item_list in all_items:
        base_date += timedelta(hours=random.randint(15, 50))  
        num_items = random.randint(0, 10)

        random_items = random.sample(item_list, num_items)
        for item in random_items:
            sequence =random.choices(interaction_sequences, weights)[0]
            current_date = base_date
            current_date += timedelta(minutes=random.randint(1, 200),days=random.randint(0, 2))  
            for action in sequence:
                current_date += timedelta(minutes=random.randint(10, 20))  
                if action == "review":
                    # Assign a random rating between 1 and 5
                    rating = random.randint(1, 5)
                    interaction_type = f"review {rating}"
                else:
                    interaction_type = action
                    
                interactions.append({
                    "UserID": user,
                    "ItemID": item["ID"],
                    "ItemName": item["Name"],
                    "Category": item["Category"],
                    "CategoryID": item["CategoryID"],
                    "InteractionType": interaction_type,
                    "Timestamp": current_date.strftime('%Y-%m-%d %H:%M:%S')
                })



interaction_df = pd.DataFrame(interactions)
interaction_df.head(30)

interaction_df.to_json('data.json', orient='records', lines=True)